(function() {
  var BufferParser;

  require('buffertools');

  BufferParser = (function() {
    var LF;

    LF = "\r\n";

    function BufferParser() {
      this.buffer = new Buffer(0);
    }

    BufferParser.prototype.parse = function(buf) {
      var index, input, jsons, ret;
      input = Buffer.isBuffer(buf) && buf || new Buffer(buf);
      this.buffer = this.buffer.concat(input);
      jsons = [];
      while ((index = this.buffer.indexOf(LF)) > -1) {
        ret = this.buffer.slice(0, index);
        this.buffer = new Buffer(this.buffer.slice(index + LF.length));
        jsons.push(JSON.parse(ret.toString('utf8')));
      }
      return jsons;
    };

    return BufferParser;

  })();

  module.exports = BufferParser;

}).call(this);
